import React, { useState, useEffect } from 'react';
import './Homepage.css';

const Homepage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [currentColorIndex, setCurrentColorIndex] = useState(0);

  const colors = [
    '#007AFF', // iOS Blue
    '#FF3B30', // iOS Red
    '#FF9500', // iOS Orange
    '#FFCC00', // iOS Yellow
    '#34C759', // iOS Green
    '#5856D6', // iOS Purple
    '#FF2D92', // iOS Pink
    '#AF52DE', // iOS Violet
  ];

  useEffect(() => {
    // Trigger the fade-in animation
    const timer = setTimeout(() => {
      setIsVisible(true);
    }, 300);

    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    // Color cycling animation
    const interval = setInterval(() => {
      setCurrentColorIndex((prevIndex) => (prevIndex + 1) % colors.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [colors.length]);

  return (
    <div className="homepage">
      <div className="eva-landing">
        <div className={`eva-container ${isVisible ? 'visible' : ''}`}>
          <div 
            className="eva-text"
            style={{ color: colors[currentColorIndex] }}
          >
            Eva
          </div>
          <div className="eva-subtitle">
            Your intelligent project companion
          </div>
          <div className="eva-tagline">
            Beautifully simple. Powerfully organized.
          </div>
        </div>

        <div className="floating-elements">
          <div className="floating-circle circle-1"></div>
          <div className="floating-circle circle-2"></div>
          <div className="floating-circle circle-3"></div>
          <div className="floating-circle circle-4"></div>
          <div className="floating-circle circle-5"></div>
        </div>

        <div className="bottom-gradient"></div>
      </div>
    </div>
  );
};

export default Homepage;
