import React from 'react';
import HeatmapCalendar from '../components/HeatmapCalendar';
import './Planning.css';

const Planning = ({
  projects,
  tasks,
  events,
  timeScale,
  currentDate,
  onTimeScaleChange,
  onCurrentDateChange,
  onProjectUpdate,
  onProjectDelete,
  onProjectEdit,
  onEventEdit,
  onEventDelete,
  onNewProject,
  onNewEvent,
  showEventTitles,
  onToggleEventTitles,
  sidebarCollapsed
}) => {
  return (
    <div className="planning">
      <div className="planning-header">
        <h1 className="planning-title">Project Planning</h1>
        <p className="planning-subtitle">Visualize project timelines and track progress with the heat calendar</p>
      </div>

      <div className="planning-content">
        <HeatmapCalendar
          projects={projects}
          tasks={tasks}
          events={events}
          timeScale={timeScale}
          currentDate={currentDate}
          onTimeScaleChange={onTimeScaleChange}
          onCurrentDateChange={onCurrentDateChange}
          onProjectUpdate={onProjectUpdate}
          onProjectDelete={onProjectDelete}
          onProjectEdit={onProjectEdit}
          onEventEdit={onEventEdit}
          onEventDelete={onEventDelete}
          onNewProject={onNewProject}
          onNewEvent={onNewEvent}
          showEventTitles={showEventTitles}
          onToggleEventTitles={onToggleEventTitles}
          sidebarCollapsed={sidebarCollapsed}
        />
      </div>
    </div>
  );
};

export default Planning;
