import React, { useState } from 'react';
import { Calendar, Clock, Check, X } from 'lucide-react';
import './HorizontalEventModal.css';

const HorizontalEventModal = ({ onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    name: '',
    date: '',
    time: '09:00',
    type: 'milestone'
  });
  const [errors, setErrors] = useState({});
  const [dateInput, setDateInput] = useState(''); // For dd/mm/yy format

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear errors when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: null
      }));
    }
  };

  // Helper function to format date from YYYY-MM-DD to dd/mm/yy
  const formatDateToShort = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    const day = date.getDate().toString().padStart(2, '0');
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    const year = date.getFullYear().toString().slice(-2); // Last 2 digits
    return `${day}/${month}/${year}`;
  };

  // Helper function to parse dd/mm/yy format
  const parseDateInput = (dateStr) => {
    const parts = dateStr.split(/[/.-]/);
    if (parts.length === 3) {
      const day = parseInt(parts[0], 10);
      const month = parseInt(parts[1], 10);
      let year = parseInt(parts[2], 10);

      // Convert 2-digit year to 4-digit year
      if (year >= 0 && year <= 30) {
        year += 2000; // 00-30 -> 2000-2030
      } else if (year >= 31 && year <= 99) {
        year += 1900; // 31-99 -> 1931-1999
      } else if (year >= 1900) {
        // Already 4-digit year, keep as is
      } else {
        return null; // Invalid year
      }

      if (day >= 1 && day <= 31 && month >= 1 && month <= 12) {
        // Convert to YYYY-MM-DD format for internal use
        return `${year}-${month.toString().padStart(2, '0')}-${day.toString().padStart(2, '0')}`;
      }
    }
    return null;
  };

  // Handle date input change
  const handleDateInputChange = (e) => {
    let value = e.target.value;

    // Remove all non-numeric characters except slashes
    value = value.replace(/[^\d/]/g, '');

    // Auto-format with slashes as user types
    if (value.length >= 2 && value.charAt(2) !== '/') {
      value = value.substring(0, 2) + '/' + value.substring(2);
    }
    if (value.length >= 5 && value.charAt(5) !== '/') {
      value = value.substring(0, 5) + '/' + value.substring(5);
    }

    // Limit to 8 characters (dd/mm/yy)
    if (value.length > 8) {
      value = value.substring(0, 8);
    }

    setDateInput(value);

    // Only try to parse if we have a complete date (8 characters: dd/mm/yy)
    if (value.length === 8) {
      const parsedDate = parseDateInput(value);
      if (parsedDate) {
        // Additional validation for deadlines - must be today or future
        const selectedDate = new Date(parsedDate);
        const today = new Date();
        today.setHours(0, 0, 0, 0); // Start of today

        if (selectedDate >= today) {
          setFormData(prev => ({ ...prev, date: parsedDate }));
          if (errors.date) {
            setErrors(prev => ({ ...prev, date: null }));
          }
        } else {
          // Clear the internal date if deadline is in the past
          setFormData(prev => ({ ...prev, date: '' }));
        }
      } else {
        // Clear the internal date if input is invalid
        setFormData(prev => ({ ...prev, date: '' }));
      }
    } else {
      // Clear the internal date if input is incomplete
      setFormData(prev => ({ ...prev, date: '' }));
    }
  };

  // Handle date key press for better UX
  const handleDateKeyPress = (e) => {
    // Allow backspace, delete, tab, escape, enter
    if ([8, 9, 27, 13, 46].indexOf(e.keyCode) !== -1 ||
        // Allow Ctrl+A, Ctrl+C, Ctrl+V, Ctrl+X
        (e.keyCode === 65 && e.ctrlKey === true) ||
        (e.keyCode === 67 && e.ctrlKey === true) ||
        (e.keyCode === 86 && e.ctrlKey === true) ||
        (e.keyCode === 88 && e.ctrlKey === true)) {
      return;
    }
    // Ensure that it is a number or slash and stop the keypress
    if ((e.shiftKey || (e.keyCode < 48 || e.keyCode > 57)) && 
        (e.keyCode < 96 || e.keyCode > 105) && 
        e.keyCode !== 191 && e.keyCode !== 111) {
      e.preventDefault();
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Event name is required';
    } else if (formData.name.trim().length < 2) {
      newErrors.name = 'Event name must be at least 2 characters';
    } else if (formData.name.trim().length > 100) {
      newErrors.name = 'Event name must be less than 100 characters';
    }

    if (!formData.date) {
      if (dateInput.trim()) {
        newErrors.date = 'Please enter a valid date in dd/mm/yy format (e.g., 25/12/24)';
      } else {
        newErrors.date = 'Date is required';
      }
    } else {
      const selectedDate = new Date(formData.date + 'T' + formData.time);
      const now = new Date();
      const today = new Date(now.getFullYear(), now.getMonth(), now.getDate()); // Start of today
      const selectedDateOnly = new Date(selectedDate.getFullYear(), selectedDate.getMonth(), selectedDate.getDate());

      // Check if date is in the past
      if (selectedDate < now) {
        newErrors.date = 'Date cannot be in the past';
      }

      // Additional validation for deadlines - must be today or after today
      if (formData.type === 'deadline' && selectedDateOnly < today) {
        newErrors.date = 'Deadlines must be set for today or a future date';
      }
    }

    if (!formData.time) {
      newErrors.time = 'Time is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = () => {
    if (validateForm()) {
      const eventDateTime = new Date(formData.date + 'T' + formData.time);

      onSave({
        name: formData.name.trim(),
        date: eventDateTime.toISOString(),
        time: formData.time,
        type: formData.type
      });
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSubmit();
    } else if (e.key === 'Escape') {
      onCancel();
    }
  };

  return (
    <div className="horizontal-event-modal" onKeyDown={handleKeyDown}>
      {/* First row: Type buttons and Event Name */}
      <div className="horizontal-event-row">
        <div className="type-buttons">
          <button
            type="button"
            className={`type-btn ${formData.type === 'milestone' ? 'active' : ''}`}
            onClick={() => setFormData(prev => ({ ...prev, type: 'milestone' }))}
            title="Milestone"
          >
            M
          </button>
          <button
            type="button"
            className={`type-btn ${formData.type === 'deadline' ? 'active' : ''}`}
            onClick={() => setFormData(prev => ({ ...prev, type: 'deadline' }))}
            title="Deadline"
          >
            D
          </button>
        </div>

        <input
          name="name"
          type="text"
          className={`event-name-input ${errors.name ? 'input-error' : ''}`}
          value={formData.name}
          onChange={handleInputChange}
          placeholder="Event name..."
          autoFocus
        />
      </div>

      {/* Second row: Date, Time, and Action buttons */}
      <div className="horizontal-event-row">
        <div className="date-time-inputs">
          <div className="date-input-wrapper">
            <Calendar size={14} className="input-icon" />
            <input
              name="date"
              type="text"
              className={`date-input ${errors.date ? 'input-error' : ''}`}
              value={dateInput}
              onChange={handleDateInputChange}
              onKeyDown={handleDateKeyPress}
              placeholder="dd/mm/yy"
              maxLength={8}
            />
          </div>

          <div className="time-input-wrapper">
            <Clock size={14} className="input-icon" />
            <input
              name="time"
              type="time"
              className={`time-input ${errors.time ? 'input-error' : ''}`}
              value={formData.time}
              onChange={handleInputChange}
            />
          </div>
        </div>

        <div className="action-buttons">
          <button
            type="button"
            className="btn-save"
            onClick={handleSubmit}
            title="Save event"
          >
            <Check size={16} />
          </button>
          <button
            type="button"
            className="btn-cancel"
            onClick={onCancel}
            title="Cancel"
          >
            <X size={16} />
          </button>
        </div>
      </div>

      {/* Error messages */}
      {(errors.name || errors.date || errors.time) && (
        <div className="horizontal-event-errors">
          {errors.name && <div className="error-text">{errors.name}</div>}
          {errors.date && <div className="error-text">{errors.date}</div>}
          {errors.time && <div className="error-text">{errors.time}</div>}
        </div>
      )}
    </div>
  );
};

export default HorizontalEventModal;
