import React, { useState, useEffect, useRef } from 'react';
import { X, Calendar, Clock, Plus } from 'lucide-react';
import { HexColorPicker } from 'react-colorful';
import EventModal from './EventModal';
import './ProjectModal.css';

// Implements requirement D5: Project creation modal with deadline and milestone support
const ProjectModal = ({ project, events = [], onSave, onClose, onEventSave, onEventDelete }) => {
  const [formData, setFormData] = useState({
    name: '',
    color: '#3b82f6'
  });
  const [errors, setErrors] = useState({});
  const [showColorPicker, setShowColorPicker] = useState(false);
  const [showEventModal, setShowEventModal] = useState(false);
  const [eventModalType, setEventModalType] = useState(null); // 'deadline' or 'milestone'
  const [editingEvent, setEditingEvent] = useState(null);
  const deadlineButtonRef = useRef(null);
  const milestoneButtonRef = useRef(null);

  // Predefined color palette
  const colorPalette = [
    '#3b82f6', '#10b981', '#8b5cf6',
    '#ec4899', '#06b6d4', '#84cc16', '#f97316', '#6366f1',
    '#14b8a6', '#f43f5e', '#8b5a2b', '#64748b', '#dc2626'
  ];

  // Initialize form data when editing
  useEffect(() => {
    if (project) {
      setFormData(prev => ({
        ...prev,
        name: project.name,
        color: project.color
      }));
    }
  }, [project]);

  // Get project-specific events
  const projectEvents = project ? events.filter(event => event.projectId === project.id) : [];
  const projectDeadlines = projectEvents.filter(event => event.type === 'deadline');
  const projectMilestones = projectEvents.filter(event => event.type === 'milestone');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear errors when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: null
      }));
    }
  };

  const handleColorChange = (color) => {
    setFormData(prev => ({
      ...prev,
      color: color
    }));
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Project name is required';
    } else if (formData.name.trim().length < 2) {
      newErrors.name = 'Project name must be at least 2 characters';
    } else if (formData.name.trim().length > 50) {
      newErrors.name = 'Project name must be less than 50 characters';
    }

    if (!formData.color) {
      newErrors.color = 'Project color is required';
    }

    // Validate that project has at least one deadline (mandatory for creation)
    if (!project && projectDeadlines.length === 0) {
      newErrors.deadline = 'A deadline is required for the project';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      const projectData = {
        name: formData.name.trim(),
        color: formData.color
      };

      onSave(projectData);
    }
  };

  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  // Handle New Deadline button click
  const handleNewDeadline = (e) => {
    e.preventDefault();
    e.stopPropagation();

    setEditingEvent(null);
    setEventModalType('deadline');
    setShowEventModal(true);
  };

  // Handle New Milestone button click
  const handleNewMilestone = (e) => {
    e.preventDefault();
    e.stopPropagation();

    setEditingEvent(null);
    setEventModalType('milestone');
    setShowEventModal(true);
  };

  // Handle editing existing events
  const handleEditEvent = (event) => {
    setEditingEvent(event);
    setEventModalType(event.type);
    setShowEventModal(true);
  };

  // Handle event modal save
  const handleEventModalSave = (eventData) => {
    // If this is a new project, we need to pass the project ID when it's created
    const enhancedEventData = {
      ...eventData,
      projectId: project?.id || 'temp-project-id' // Will be replaced when project is created
    };

    if (onEventSave) {
      onEventSave(enhancedEventData, editingEvent?.id);
    }

    setShowEventModal(false);
    setEditingEvent(null);
    setEventModalType(null);
  };

  // Handle event modal close
  const handleEventModalClose = () => {
    setShowEventModal(false);
    setEditingEvent(null);
    setEventModalType(null);
  };

  // Handle event deletion
  const handleEventDelete = (eventId) => {
    if (onEventDelete) {
      onEventDelete(eventId);
    }
  };

  // Format event date for display
  const formatEventDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="modal-overlay" onClick={handleOverlayClick}>
      <div className="modal-content project-modal">
        <div className="modal-header">
          <h3>{project ? 'Edit Project' : 'Create New Project'}</h3>
          <button className="btn btn-ghost btn-sm" onClick={onClose}>
            <X size={16} />
          </button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="modal-body">
            {/* Project name input */}
            <div className="form-group">
              <label htmlFor="projectName" className="form-label">
                Project Name *
              </label>
              <input
                id="projectName"
                name="name"
                type="text"
                className={`input ${errors.name ? 'input-error' : ''}`}
                value={formData.name}
                onChange={handleInputChange}
                placeholder="Enter project name..."
                autoFocus
              />
              {errors.name && (
                <div className="form-error">{errors.name}</div>
              )}
            </div>

            {/* Color selection */}
            <div className="form-group">
              <label className="form-label">
                Project Color *
              </label>

              {/* Color palette */}
              <div className="color-palette">
                {colorPalette.map((color) => (
                  <button
                    key={color}
                    type="button"
                    className={`color-swatch ${formData.color === color ? 'selected' : ''}`}
                    style={{ backgroundColor: color }}
                    onClick={() => handleColorChange(color)}
                    title={`Select ${color}`}
                  />
                ))}
                <button
                  type="button"
                  className={`color-swatch custom-color ${showColorPicker ? 'selected' : ''}`}
                  onClick={() => setShowColorPicker(!showColorPicker)}
                  title="Custom color"
                >
                  <span>+</span>
                </button>
              </div>

              {/* Custom color picker */}
              {showColorPicker && (
                <div className="color-picker-container">
                  <HexColorPicker 
                    color={formData.color} 
                    onChange={handleColorChange}
                  />
                  <div className="color-picker-info">
                    <input
                      type="text"
                      className="input color-input"
                      value={formData.color}
                      onChange={(e) => handleColorChange(e.target.value)}
                      placeholder="#000000"
                    />
                  </div>
                </div>
              )}

              {/* Color preview */}
              <div className="color-preview">
                <span className="color-preview-label">Selected color:</span>
                <div 
                  className="color-preview-swatch"
                  style={{ backgroundColor: formData.color }}
                />
                <span className="color-preview-value">{formData.color}</span>
              </div>

              {errors.color && (
                <div className="form-error">{errors.color}</div>
              )}
            </div>

            {/* Deadline section */}
            <div className="form-group">
              <label className="form-label">
                Deadline *
              </label>

              <div className="events-section">
                <div className="events-list">
                  {projectDeadlines.length > 0 ? (
                    projectDeadlines.map(deadline => (
                      <div key={deadline.id} className="event-item deadline-item">
                        <div className="event-info">
                          <span className="event-name">{deadline.name}</span>
                          <span className="event-date">{formatEventDate(deadline.date)}</span>
                        </div>
                        <div className="event-actions">
                          <button
                            type="button"
                            className="btn btn-ghost btn-sm"
                            onClick={() => handleEditEvent(deadline)}
                          >
                            Edit
                          </button>
                          <button
                            type="button"
                            className="btn btn-ghost btn-sm btn-danger"
                            onClick={() => handleEventDelete(deadline.id)}
                          >
                            Delete
                          </button>
                        </div>
                      </div>
                    ))
                  ) : (
                    <div className="no-events-message">
                      No deadline set. A deadline is required for the project.
                    </div>
                  )}
                </div>

                <button
                  type="button"
                  className="btn btn-secondary btn-add-event"
                  ref={deadlineButtonRef}
                  onClick={handleNewDeadline}
                >
                  <Plus size={16} />
                  {projectDeadlines.length > 0 ? 'Change Deadline' : 'Set Deadline'}
                </button>
              </div>

              {errors.deadline && (
                <div className="form-error">{errors.deadline}</div>
              )}
            </div>

            {/* Milestones section */}
            <div className="form-group">
              <label className="form-label">
                Milestones
              </label>

              <div className="events-section">
                <div className="events-list">
                  {projectMilestones.length > 0 ? (
                    projectMilestones.map(milestone => (
                      <div key={milestone.id} className="event-item milestone-item">
                        <div className="event-info">
                          <span className="event-name">{milestone.name}</span>
                          <span className="event-date">{formatEventDate(milestone.date)}</span>
                        </div>
                        <div className="event-actions">
                          <button
                            type="button"
                            className="btn btn-ghost btn-sm"
                            onClick={() => handleEditEvent(milestone)}
                          >
                            Edit
                          </button>
                          <button
                            type="button"
                            className="btn btn-ghost btn-sm btn-danger"
                            onClick={() => handleEventDelete(milestone.id)}
                          >
                            Delete
                          </button>
                        </div>
                      </div>
                    ))
                  ) : (
                    <div className="no-events-message">
                      No milestones added. Milestones are optional but help track progress.
                    </div>
                  )}
                </div>

                <button
                  type="button"
                  className="btn btn-secondary btn-add-event"
                  ref={milestoneButtonRef}
                  onClick={handleNewMilestone}
                >
                  <Plus size={16} />
                  Add Milestone
                </button>
              </div>
            </div>
          </div>

          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" onClick={onClose}>
              Cancel
            </button>
            <button type="submit" className="btn btn-primary">
              {project ? 'Update Project' : 'Create Project'}
            </button>
          </div>
        </form>

        {/* Event Modal */}
        {showEventModal && (
          <EventModal
            event={editingEvent}
            projects={project ? [{ ...project, ...formData }] : [{ id: 'temp', name: formData.name || 'New Project', color: formData.color }]}
            onSave={handleEventModalSave}
            onClose={handleEventModalClose}
            forcedType={eventModalType}
          />
        )}
      </div>
    </div>
  );
};

export default ProjectModal;
