import React, { useState } from 'react';
import './Sidebar.css';

const Sidebar = ({ isCollapsed, onToggle }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const navigationItems = [
    { 
      id: 'dashboard', 
      label: 'Dashboard', 
      icon: '📊', 
      isActive: true 
    },
    { 
      id: 'projects', 
      label: 'Projects', 
      icon: '📁', 
      isActive: false 
    },
    { 
      id: 'tasks', 
      label: 'Tasks', 
      icon: '✓', 
      isActive: false 
    },
    { 
      id: 'calendar', 
      label: 'Calendar', 
      icon: '📅', 
      isActive: false 
    },
    { 
      id: 'analytics', 
      label: 'Analytics', 
      icon: '📈', 
      isActive: false 
    },
    { 
      id: 'settings', 
      label: 'Settings', 
      icon: '⚙️', 
      isActive: false 
    }
  ];

  const evaFeatures = [
    { 
      id: 'assessment', 
      label: 'Assessment', 
      icon: '🎯', 
      isActive: false 
    },
    { 
      id: 'ai-insights', 
      label: 'AI Insights', 
      icon: '🤖', 
      isActive: false 
    },
    { 
      id: 'knowledge-base', 
      label: 'Knowledge Base', 
      icon: '📚', 
      isActive: false 
    },
    { 
      id: 'progress-tracking', 
      label: 'Progress', 
      icon: '📊', 
      isActive: false 
    }
  ];

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleItemClick = (itemId) => {
    console.log(`Navigation to ${itemId}`);
    // Navigation logic will be implemented when routing is added
  };

  return (
    <>
      {/* Sidebar overlay for mobile */}
      {!isCollapsed && (
        <div 
          className="sidebar-overlay"
          onClick={onToggle}
        />
      )}

      <aside className={`sidebar ${isCollapsed ? 'sidebar-collapsed' : 'sidebar-expanded'}`}>
        {/* Sidebar Header */}
        <div className="sidebar-header">
          <div className="sidebar-brand">
            <span className="brand-icon">🔥</span>
            {!isCollapsed && <span className="brand-text">EVA</span>}
          </div>
          <button 
            className="sidebar-toggle"
            onClick={onToggle}
            aria-label={isCollapsed ? 'Expand sidebar' : 'Collapse sidebar'}
          >
            {isCollapsed ? '→' : '←'}
          </button>
        </div>

        {/* Search Bar */}
        {!isCollapsed && (
          <div className="sidebar-search">
            <div className="search-container">
              <span className="search-icon">🔍</span>
              <input
                type="text"
                placeholder="Search..."
                value={searchTerm}
                onChange={handleSearch}
                className="search-input"
              />
            </div>
          </div>
        )}

        <div className="sidebar-content">
          {/* Main Navigation */}
          <nav className="sidebar-nav">
            <div className="nav-section">
              {!isCollapsed && <h3 className="nav-title">Navigation</h3>}
              <ul className="nav-list">
                {navigationItems.map((item) => (
                  <li key={item.id} className="nav-item">
                    <button
                      className={`nav-link ${item.isActive ? 'nav-link-active' : ''}`}
                      onClick={() => handleItemClick(item.id)}
                      title={isCollapsed ? item.label : undefined}
                    >
                      <span className="nav-icon">{item.icon}</span>
                      {!isCollapsed && <span className="nav-label">{item.label}</span>}
                    </button>
                  </li>
                ))}
              </ul>
            </div>

            {/* EVA Features Section */}
            <div className="nav-section">
              {!isCollapsed && <h3 className="nav-title">EVA Features</h3>}
              <ul className="nav-list">
                {evaFeatures.map((item) => (
                  <li key={item.id} className="nav-item">
                    <button
                      className={`nav-link ${item.isActive ? 'nav-link-active' : ''}`}
                      onClick={() => handleItemClick(item.id)}
                      title={isCollapsed ? item.label : undefined}
                    >
                      <span className="nav-icon">{item.icon}</span>
                      {!isCollapsed && <span className="nav-label">{item.label}</span>}
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          </nav>

          {/* Quick Stats - Only show when expanded */}
          {!isCollapsed && (
            <div className="sidebar-stats">
              <h3 className="stats-title">Quick Stats</h3>
              <div className="stats-grid">
                <div className="stat-item">
                  <span className="stat-icon">📋</span>
                  <div className="stat-content">
                    <span className="stat-value">12</span>
                    <span className="stat-label">Active Tasks</span>
                  </div>
                </div>
                <div className="stat-item">
                  <span className="stat-icon">🎯</span>
                  <div className="stat-content">
                    <span className="stat-value">85%</span>
                    <span className="stat-label">Completion</span>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Sidebar Footer */}
        <div className="sidebar-footer">
          {!isCollapsed && (
            <div className="user-profile">
              <div className="user-avatar">👤</div>
              <div className="user-info">
                <span className="user-name">User</span>
                <span className="user-role">Student</span>
              </div>
            </div>
          )}
          {isCollapsed && (
            <button className="user-avatar-collapsed" title="User Profile">
              👤
            </button>
          )}
        </div>
      </aside>
    </>
  );
};

export default Sidebar;
