import React, { useState, useEffect } from 'react';
import { X, Plus } from 'lucide-react';
import { HexColorPicker } from 'react-colorful';
import InlineEventEditor from './InlineEventEditor';
import EventDisplay from './EventDisplay';
import './ProjectModal.css';

// Implements requirement D5: Project creation modal with events support
const ProjectModal = ({ project, projectEvents, onSave, onEventCreate, onEventDelete, onClose }) => {
  const [formData, setFormData] = useState({
    name: '',
    color: '#3b82f6'
  });
  const [errors, setErrors] = useState({});
  const [showColorPicker, setShowColorPicker] = useState(false);
  const [showInlineEditor, setShowInlineEditor] = useState(false);

  // Predefined color palette
  const colorPalette = [
    '#3b82f6', '#10b981', '#8b5cf6',
    '#ec4899', '#06b6d4', '#84cc16', '#f97316', '#6366f1',
    '#14b8a6', '#f43f5e', '#8b5a2b', '#64748b', '#dc2626'
  ];

  // Initialize form data when editing
  useEffect(() => {
    if (project) {
      setFormData({
        name: project.name,
        color: project.color
      });
    }
  }, [project]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear errors when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: null
      }));
    }
  };

  const handleColorChange = (color) => {
    setFormData(prev => ({
      ...prev,
      color: color
    }));
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Project name is required';
    } else if (formData.name.trim().length < 2) {
      newErrors.name = 'Project name must be at least 2 characters';
    } else if (formData.name.trim().length > 50) {
      newErrors.name = 'Project name must be less than 50 characters';
    }

    if (!formData.color) {
      newErrors.color = 'Project color is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      const projectData = {
        name: formData.name.trim(),
        color: formData.color
      };

      onSave(projectData);
    }
  };

  const handleNewEventClick = () => {
    setShowInlineEditor(true);
  };

  const handleEventSave = async (eventData) => {
    try {
      await onEventCreate(eventData);
      setShowInlineEditor(false);
    } catch (error) {
      console.error('Error creating event:', error);
      // Error handling is done in the parent component
    }
  };

  const handleEventCancel = () => {
    setShowInlineEditor(false);
  };

  const handleEventDelete = async (eventId) => {
    if (window.confirm('Are you sure you want to delete this event?')) {
      try {
        await onEventDelete(eventId);
      } catch (error) {
        console.error('Error deleting event:', error);
        // Error handling is done in the parent component
      }
    }
  };

  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  // Sort events by date for better display
  const sortedEvents = [...projectEvents].sort((a, b) => new Date(a.date) - new Date(b.date));

  return (
    <div className="modal-overlay" onClick={handleOverlayClick}>
      <div className="modal-content project-modal">
        <div className="modal-header">
          <h3>{project ? 'Edit Project' : 'Create New Project'}</h3>
          <button className="btn btn-ghost btn-sm" onClick={onClose}>
            <X size={16} />
          </button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="modal-body">
            {/* Project name input */}
            <div className="form-group">
              <label htmlFor="projectName" className="form-label">
                Project Name *
              </label>
              <input
                id="projectName"
                name="name"
                type="text"
                className={`input ${errors.name ? 'input-error' : ''}`}
                value={formData.name}
                onChange={handleInputChange}
                placeholder="Enter project name..."
                autoFocus
              />
              {errors.name && (
                <div className="form-error">{errors.name}</div>
              )}
            </div>

            {/* Color selection */}
            <div className="form-group">
              <label className="form-label">
                Project Color *
              </label>

              {/* Color palette */}
              <div className="color-palette">
                {colorPalette.map((color) => (
                  <button
                    key={color}
                    type="button"
                    className={`color-swatch ${formData.color === color ? 'selected' : ''}`}
                    style={{ backgroundColor: color }}
                    onClick={() => handleColorChange(color)}
                    title={`Select ${color}`}
                  />
                ))}
                <button
                  type="button"
                  className={`color-swatch custom-color ${showColorPicker ? 'selected' : ''}`}
                  onClick={() => setShowColorPicker(!showColorPicker)}
                  title="Custom color"
                >
                  <span>+</span>
                </button>
              </div>

              {/* Custom color picker */}
              {showColorPicker && (
                <div className="color-picker-container">
                  <HexColorPicker 
                    color={formData.color} 
                    onChange={handleColorChange}
                  />
                  <div className="color-picker-info">
                    <input
                      type="text"
                      className="input color-input"
                      value={formData.color}
                      onChange={(e) => handleColorChange(e.target.value)}
                      placeholder="#000000"
                    />
                  </div>
                </div>
              )}

              {/* Color preview */}
              <div className="color-preview">
                <span className="color-preview-label">Selected color:</span>
                <div 
                  className="color-preview-swatch"
                  style={{ backgroundColor: formData.color }}
                />
                <span className="color-preview-value">{formData.color}</span>
              </div>

              {errors.color && (
                <div className="form-error">{errors.color}</div>
              )}
            </div>

            {/* Events section */}
            <div className="form-group">
              <label className="form-label">
                Events
              </label>

              <div className="events-section">
                {/* Inline event editor - shown when creating new event */}
                {showInlineEditor && (
                  <InlineEventEditor
                    projectId={project?.id}
                    onSave={handleEventSave}
                    onCancel={handleEventCancel}
                  />
                )}

                {/* New Event button */}
                {!showInlineEditor && (
                  <button
                    type="button"
                    className="btn btn-secondary new-event-btn"
                    onClick={handleNewEventClick}
                  >
                    <Plus size={16} />
                    New Event
                  </button>
                )}

                {/* Display existing events */}
                {sortedEvents.length > 0 && (
                  <div className="events-list">
                    {sortedEvents.map(event => (
                      <EventDisplay
                        key={event.id}
                        event={event}
                        onDelete={handleEventDelete}
                      />
                    ))}
                  </div>
                )}

                {/* Empty state for events */}
                {sortedEvents.length === 0 && !showInlineEditor && (
                  <div className="empty-events-state">
                    <p>No events created yet. Add milestones and deadlines to track project progress.</p>
                  </div>
                )}
              </div>
            </div>
          </div>

          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" onClick={onClose}>
              Cancel
            </button>
            <button type="submit" className="btn btn-primary">
              {project ? 'Update Project' : 'Create Project'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProjectModal;
